		ServletContextTemplateResolver resolver = new ServletContextTemplateResolver();
		resolver.setTemplateMode("HTML5");
		resolver.setCharacterEncoding("utf-8");
		resolver.setCacheable(false);
		resolver.setOrder(2);
		return resolver;
	}

&lt;p&gt;	public SpringTemplateEngine templateEngine() &lt;/p&gt;
{
		SpringTemplateEngine engine = new SpringTemplateEngine();
		engine.setTemplateResolver(templateResolver());
		engine.addDialect(new TilesDialect()); // Tiles
		engine.addDialect(new SpringSecurityDialect()); // Tiles
		engine.addDialect(new ConditionalCommentsDialect()); // Tiles
                //engine.setMessageSource(messageSource());
		return engine;
	}

&lt;p&gt;       @Bean&lt;br/&gt;
	public ViewResolver viewResolver() {&lt;/p&gt;

&lt;p&gt;		ThymeleafViewResolver delegate = new ThymeleafViewResolver();&lt;br/&gt;
		delegate.setViewClass(ThymeleafTilesView.class); // Tiles&lt;br/&gt;
		delegate.setTemplateEngine(templateEngine());&lt;br/&gt;
		delegate.setCharacterEncoding("UTF-8");&lt;br/&gt;
		delegate.setOrder(1);&lt;br/&gt;
		delegate.setViewNames(new String[] &lt;/p&gt;
{ "*" }
&lt;p&gt;);&lt;br/&gt;
		delegate.setCache(false); &lt;br/&gt;
                return delegate;&lt;br/&gt;
	}&lt;/p&gt;

&lt;p&gt;	@Bean&lt;br/&gt;
	public ThymeleafTilesConfigurer thymeleafTilesConfigurer() {&lt;br/&gt;
		ThymeleafTilesConfigurer ttc = new ThymeleafTilesConfigurer();&lt;br/&gt;
		ttc.setDefinitions(new String[] &lt;/p&gt;
{ "/WEB-INF/defs/views.xml" }
&lt;p&gt;);&lt;br/&gt;
		return ttc;&lt;br/&gt;
	}&lt;/p&gt;

&lt;p&gt;       @Bean&lt;br/&gt;
	public MessageSource messageSource() &lt;/p&gt;
{

		ReloadableResourceBundleMessageSource messageSource = new ReloadableResourceBundleMessageSource();
		messageSource.setBasenames("/WEB-INF/messages/messages");
		messageSource.setUseCodeAsDefaultMessage(true);
		messageSource.setDefaultEncoding("UTF-8");		
		messageSource.setCacheSeconds(0);
		return messageSource;
	}

