&lt;p&gt;            Class _targetClass = AopUtils.getTargetClass(obj);&lt;br/&gt;
            Class[] _targetInterfaces = _targetClass.getInterfaces();&lt;br/&gt;
            Method method = null;&lt;br/&gt;
